import { createContext } from "react";

export default createContext();
import React, { useEffect, useState } from "react";
import HSLSliderContext from "./HSLSliderContext";

export default ({ children, onChange = () => {} }) => {
  const [hue, setHue] = useState(0);
  const [saturation, setSaturation] = useState(1);
  const [lightness, setLightness] = useState(0.5);

  return (
    <HSLSliderContext.Provider
      value={{
        hue,
        setHue: (hue) => {
          setHue(hue);
          onChange(hue, saturation, lightness);
        },
        saturation,
        setSaturation: (saturation) => {
          setSaturation(saturation);
          onChange(hue, saturation, lightness);
        },
        lightness,
        setLightness: (lightness) => {
          setLightness(lightness);
          onChange(hue, saturation, lightness);
        },
      }}
    >
      {children}
    </HSLSliderContext.Provider>
  );
};
import React, { useContext } from "react";
import Slider from "@material-ui/core/Slider";
import makeStyles from "@material-ui/core/styles/makeStyles";
import tinyColor from "tinycolor2";
import HSLSliderContext from "./HSLSliderContext";

const useStyles = makeStyles({
  rail: {
    background:
      "linear-gradient(to right, #f00, #ff0, #0f0, #0ff, #00f, #f0f, #f00)",
    height: "8px",
    borderRadius: "4px",
    opacity: 1,
  },
  thumb: {
    width: "20px",
    height: "20px",
    marginTop: "-6px",
    color: "white",
    boxShadow: ({ hue }) =>
      "0px 0px 0px 2px " + tinyColor({ h: hue, s: 1, l: 0.5 }),
    "&:hover": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 8px " + tinyColor({ h: hue, s: 1, l: 0.5 }).setAlpha(0.16),
    },
    "&.Mui-focusVisible": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 8px " + tinyColor({ h: hue, s: 1, l: 0.5 }).setAlpha(0.16),
    },
    "&.MuiSlider-active": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 14px " +
        tinyColor({ h: hue, s: 1, l: 0.5 }).setAlpha(0.16),
    },
  },
});

export default ({
  style,
  onChange = () => {},
  onChangeCommitted = () => {},
}) => {
  const { hue, setHue } = useContext(HSLSliderContext);
  const { rail, thumb } = useStyles({ hue });

  return (
    <Slider
      track={false}
      classes={{ rail, thumb }}
      min={0}
      max={359}
      style={style}
      onChange={(_, value) => {
        setHue(value);
        onChange(value);
      }}
      value={hue}
      onChangeCommitted={onChangeCommitted}
    />
  );
};
