import React, { useEffect, useState } from "react";
import RGBSliderContext from "./RGBSliderContext";

export default ({ children, onChange = () => {} }) => {
  const [red, setRed] = useState(127);
  const [green, setGreen] = useState(127);
  const [blue, setBlue] = useState(127);

  return (
    <RGBSliderContext.Provider
      value={{
        red,
        setRed: (red) => {
          setRed(red);
          onChange(red, green, blue);
        },
        green,
        setGreen: (green) => {
          setGreen(green);
          onChange(red, green, blue);
        },
        blue,
        setBlue: (blue) => {
          setBlue(blue);
          onChange(red, green, blue);
        },
      }}
    >
      {children}
    </RGBSliderContext.Provider>
  );
};
