import React, { useState } from "react";
import {
  HSLSliderProvider,
  HueSlider,
  SaturationSlider,
  LightnessSlider,
  RGBSliderProvider,
  RedSlider,
  BlueSlider,
  GreenSlider,
} from "material-ui-color-sliders";
import Typography from "@material-ui/core/Typography";

export default () => {
  const [HSL, setHSL] = useState("hsl(0, 0, 0)");

  return (
    <div style={{ margin: "32px 64px" }}>
      <Typography variant="h3" style={{ color: HSL }}>
        HSL Sliders
      </Typography>
      <HSLSliderProvider
        onChange={(hue, saturation, lightness) =>
          setHSL(`hsl(${hue}, ${saturation * 100}%, ${lightness * 100}%)`)
        }
      >
        <HueSlider style={{ margin: "32px 0" }} />
        <SaturationSlider style={{ margin: "32px 0" }} />
        <LightnessSlider style={{ margin: "32px 0" }} />
      </HSLSliderProvider>
      <Typography variant="h3" style={{ marginTop: "32px" }}>
        RGB Sliders
      </Typography>
      <RGBSliderProvider>
        <RedSlider style={{ margin: "32px 0" }} />
        <GreenSlider style={{ margin: "32px 0" }} />
        <BlueSlider style={{ margin: "32px 0" }} />
      </RGBSliderProvider>
    </div>
  );
};
