import React, { useState } from "react";
import {
  HSLSliderProvider,
  HueSlider,
  SaturationSlider,
  LightnessSlider,
  RGBSliderProvider,
  RedSlider,
  BlueSlider,
  GreenSlider,
} from "material-ui-color-sliders";
import Typography from "@material-ui/core/Typography";

export default () => {
  const [{ hue, saturation, lightness }, setHSL] = useState({
    hue: 0,
    saturation: 0,
    lightness: 0,
  });

  return (
    <div style={{ margin: "32px 64px" }}>
      <Typography
        variant="h3"
        style={{ color: `hsl(${hue}, ${saturation}, ${lightness})` }}
      >
        HSL
      </Typography>
      <HSLSliderProvider onChange={setHSL}>
        <HueSlider style={{ margin: "32px 0" }} />
        <SaturationSlider style={{ margin: "32px 0" }} />
        <LightnessSlider style={{ margin: "32px 0" }} />
      </HSLSliderProvider>
      <Typography variant="h3" style={{ marginTop: "32px" }}>
        RGB
      </Typography>
      <RGBSliderProvider>
        <RedSlider style={{ margin: "32px 0" }} />
        <GreenSlider style={{ margin: "32px 0" }} />
        <BlueSlider style={{ margin: "32px 0" }} />
      </RGBSliderProvider>
    </div>
  );
};
