import { createContext } from "react";

export default createContext();
import React, { useState } from "react";
import ColorSliderContext from "./ColorSliderContext";

export default ({ children }) => {
  const [hue, setHue] = useState("#f00");

  return (
    <ColorSliderContext.Provider value={{ hue, setHue }}>
      {children}
    </ColorSliderContext.Provider>
  );
};
import React, { useContext } from "react";
import Slider from "@material-ui/core/Slider";
import makeStyles from "@material-ui/core/styles/makeStyles";
import tinyColor from "tinycolor2";
import ColorSliderContext from "./ColorSliderContext";

const useStyles = makeStyles({
  rail: {
    background:
      "linear-gradient(to right, #f00, #ff0, #0f0, #0ff, #00f, #f0f, #f00)",
    height: "8px",
    borderRadius: "4px",
    opacity: 1,
  },
  thumb: {
    width: "20px",
    height: "20px",
    marginTop: "-6px",
    color: ({ hue }) => tinyColor({ h: hue, s: 1, l: 0.5 }),
    "&:hover": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 8px " + tinyColor({ h: hue, s: 1, l: 0.5 }).setAlpha(0.16),
    },
    "&.Mui-focusVisible": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 8px " + tinyColor({ h: hue, s: 1, l: 0.5 }).setAlpha(0.16),
    },
    "&.MuiSlider-active": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 14px " +
        tinyColor({ h: hue, s: 1, l: 0.5 }).setAlpha(0.16),
    },
  },
});

export default ({ style }) => {
  const { hue, setHue } = useContext(ColorSliderContext);
  const { rail, thumb } = useStyles({ hue });

  return (
    <Slider
      track={false}
      classes={{ rail, thumb }}
      min={0}
      max={359}
      style={style}
      onChange={(_, value) => setHue(value)}
    />
  );
};
import React, { useContext } from "react";
import Slider from "@material-ui/core/Slider";
import makeStyles from "@material-ui/core/styles/makeStyles";
import tinyColor from "tinycolor2";
import ColorSliderContext from "./ColorSliderContext";

const useStyles = makeStyles({
  rail: {
    background: ({ hue }) => `linear-gradient(to right, #fff, ${hue})`,
    height: "8px",
    borderRadius: "4px",
    opacity: 1,
  },
  thumb: {
    width: "20px",
    height: "20px",
    marginTop: "-6px",
    color: ({ hue }) => hue,
    "&:hover": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 8px " + tinyColor(hue).setAlpha(0.16),
    },
    "&.Mui-focusVisible": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 8px " + tinyColor(hue).setAlpha(0.16),
    },
    "&.MuiSlider-active": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 14px " + tinyColor(hue).setAlpha(0.16),
    },
  },
});

const convertToHex = (value) => {
  const hex = value.toString(16);

  return hex.length === 2 ? hex : "0" + hex;
};

export default ({ style }) => {
  const { hue } = useContext(ColorSliderContext);
  const { rail, thumb } = useStyles({ hue });

  return (
    <Slider
      track={false}
      classes={{ rail, thumb }}
      min={0}
      max={1535}
      style={style}
    />
  );
};
