import React, { useState } from "react";
import Slider from "@material-ui/core/Slider";
import makeStyles from "@material-ui/core/styles/makeStyles";
import tinyColor from "tinycolor2";

const useStyles = makeStyles({
  rail: {
    background:
      "linear-gradient(to right, #f00 0%, #ff0 17%, #0f0 33%, #0ff 50%, #00f 67%, #f0f 83%, #f00 100%)",
    height: "8px",
    borderRadius: "4px",
  },
  thumb: {
    width: "20px",
    height: "20px",
    marginTop: "-6px",
    color: ({ hue }) => hue,
    "&:hover": {
      boxShadow: ({ hue }) =>
        "0px 0px 0px 8px " + tinyColor(hue).setAlpha(0.16),
    },
  },
});

const convertToHex = (value) => {
  const hex = (value % 256).toString(16);

  return hex.length === 2 ? hex : "0" + hex;
};

export default () => {
  const [hue, setHue] = useState("#ff0000");
  const { rail, thumb } = useStyles({ hue });
  console.log(hue);
  return (
    <Slider
      track={false}
      classes={{ rail, thumb }}
      min={0}
      max={1536}
      onChange={(_, value) =>
        setHue(
          value % 256 === 0
            ? `#ff${convertToHex(value)}00`
            : `#${convertToHex(value)}ff00`
        )
      }
    />
  );
};
