import React, { useState } from "react";
import {
  HSLSliderProvider,
  HueSlider,
  SaturationSlider,
  LightnessSlider,
  RGBSliderProvider,
  RedSlider,
  BlueSlider,
  GreenSlider,
} from "material-ui-color-sliders";
import Typography from "@material-ui/core/Typography";

export default () => {
  const [HSL, setHSL] = useState("hsl(0, 0, 0)");
  const [RGB, setRGB] = useState("rgb(0, 0, 0)");

  return (
    <div style={{ margin: "32px 64px" }}>
      <Typography variant="h3" style={{ color: HSL }}>
        HSL
      </Typography>
      <HSLSliderProvider onChange={console.log}>
        <HueSlider style={{ margin: "32px 0" }} defaultValue={200} />
        <SaturationSlider style={{ margin: "32px 0" }} defaultValue={0.6} />
        <LightnessSlider style={{ margin: "32px 0" }} defaultValue={0.4} />
      </HSLSliderProvider>
      <Typography variant="h3" style={{ color: RGB, marginTop: "32px" }}>
        RGB
      </Typography>
      <RGBSliderProvider
        onChange={(red, green, blue) =>
          setRGB(`rgb(${red}, ${green}, ${blue})`)
        }
      >
        <RedSlider style={{ margin: "32px 0" }} defaultValue={200} />
        <GreenSlider style={{ margin: "32px 0" }} defaultValue={100} />
        <BlueSlider style={{ margin: "32px 0" }} defaultValue={150} />
      </RGBSliderProvider>
    </div>
  );
};
